body {
    height: 100%;
    background: #4A7FB1;
    overflow: hidden;
}

#display {
    color: white;
    font-family: Helvetica, sans-serif;
    position: absolute;
    transform: translate(-50%, -50%);
    font-weight: bold;
    white-space: nowrap;
    /* Before the Javascript's fired, keep it off-screen */
    left: -100000px;
    top: -100000px;
}

#display img {
    height: 24px;
    width: auto;
}
